mapper.select:
SELECT status
FROM vika_billing_social_feishu_order
WHERE order_id = #{orderId}
AND tenant_id = #{tenantId}
AND app_id = #{appId}

SELECT order_data
FROM vika_billing_social_feishu_order
WHERE tenant_id = #{tenantId}
AND app_id = #{appId}
AND `status` = 0;

mapper.update:
UPDATE vika_billing_social_feishu_order
SET `status` = #{status}
WHERE tenant_id = #{tenantId}
AND app_id = #{appId}
AND `order_id` = #{orderId};
