mapper.select:
SELECT msa.app_id
FROM vika_marketplace_space_app_rel msa
WHERE msa.space_id = #{spaceId}

SELECT count(*)
FROM vika_marketplace_space_app_rel msa
WHERE msa.space_id = #{spaceId} and msa.app_id = #{appId}

mapper.delete:
DELETE
FROM vika_marketplace_space_app_rel msa
WHERE msa.space_id = #{spaceId} and msa.app_id = #{appId}
