mapper.select:
SELECT id AS cpTenantUserId, cp_user_id AS cpUserId
FROM vika_social_cp_tenant_user
WHERE tenant_id = #{tenantId}
  AND app_id = #{appId}

SELECT *
FROM vika_social_cp_tenant_user
WHERE tenant_id = #{tenantId}
  AND app_id = #{appId}
  AND cp_user_id = #{cpUserId}

SELECT *
FROM vika_social_cp_tenant_user
WHERE tenant_id = #{tenantId}
  AND app_id = #{appId}
  AND cp_user_id IN

    #{cpUserId}

SELECT vsctu.*
FROM (
    SELECT cp_tenant_user_id
    FROM `vika_social_cp_user_bind`
    WHERE `user_id` = #{userId}
) vscub INNER JOIN (
    SELECT *
    FROM `vika_social_cp_tenant_user`
    WHERE `tenant_id` = #{tenantId}
    AND `app_id` = #{appId}
) vsctu ON vscub.cp_tenant_user_id = vsctu.id

SELECT id
FROM vika_social_cp_tenant_user
WHERE tenant_id = #{tenantId}
  AND app_id = #{appId}
  AND cp_user_id = #{cpUserId}
