mapper.select:
SELECT COUNT(*)
FROM vika_social_feishu_event_log
WHERE uuid = #{uuid}

mapper.update:
UPDATE vika_social_feishu_event_log
SET status = 1
WHERE uuid = #{uuid}

mapper.select:
SELECT event_data
FROM vika_social_feishu_event_log
WHERE tenant_key = #{tenantKey}
  AND type = #{type}
ORDER BY id DESC
LIMIT 0,1
