mapper.select:
SELECT *
FROM ${tablePrefix}social_tenant
WHERE app_id = #{appId} AND tenant_id = #{tenantId}

SELECT COUNT(*)
FROM ${tablePrefix}social_tenant
WHERE app_id = #{appId}
AND tenant_id = #{tenantId}

mapper.update:
UPDATE ${tablePrefix}social_tenant
SET status = 1
WHERE app_id = #{appId}
AND tenant_id = #{tenantId}

UPDATE ${tablePrefix}social_tenant
SET status = 0
WHERE app_id = #{appId}
AND tenant_id = #{tenantId}

UPDATE ${tablePrefix}social_tenant
SET status = 1, contact_auth_scope = #{scope}
WHERE tenant_id = #{tenantId}

mapper.select:
SELECT auth_info ->> '$.appAuthInfo.agentId'
FROM ${tablePrefix}social_tenant
WHERE tenant_id = #{tenantId}
AND app_id = #{appId}
AND status = 1

mapper.update:
UPDATE ${tablePrefix}social_tenant
SET status = #{enabled}
WHERE app_id = #{appId} AND tenant_id = #{tenantId}

mapper.select:
SELECT status
FROM ${tablePrefix}social_tenant
WHERE tenant_id = #{tenantId}
AND app_id = #{appId}

SELECT * FROM ${tablePrefix}social_tenant WHERE tenant_id IN
#{item}
AND status = 1

SELECT *
from ${tablePrefix}social_tenant
WHERE platform = #{platformType.value}
AND app_type = #{appType.type}

