FROM ${tablePrefix}social_tenant
WHERE app_id = #{appId}
AND tenant_id = #{tenantId}
AND status = 1

SELECT created_at
FROM ${tablePrefix}social_tenant
WHERE app_id = #{appId}
AND tenant_id = #{tenantId}
