mapper.select:
SELECT count(*) FROM vika_social_tenant_order
WHERE channel_order_id = #{channelOrderId}
AND tenant_id = #{tenantId}
AND app_id = #{appId}
AND platform = #{platform.value}
AND is_deleted = 0

SELECT order_data FROM vika_social_tenant_order
WHERE tenant_id = #{tenantId}
AND app_id = #{appId}
AND platform = #{platform.value}
AND is_deleted = 0
ORDER BY id DESC
