mapper.select:
SELECT user_id
FROM ${tablePrefix}user_link
WHERE union_id = #{unionId}
AND `type` = #{type}
LIMIT 1

SELECT union_id
FROM ${tablePrefix}user_link
WHERE user_id = #{userId}
AND `type` = #{type}
AND union_id IS NOT NULL
LIMIT 1

mapper.update:
