mapper.update:
UPDATE vika_space_asset
SET cite = #{cite}
WHERE id IN

    #{item}

UPDATE vika_space_asset SET is_deleted = 1 WHERE node_id IN
#{item}
AND is_deleted = 0

mapper.select:
SELECT vsa.id, va.checksum, va.file_url, va.bucket
FROM vika_space_asset vsa
LEFT JOIN vika_asset va ON vsa.asset_checksum = va.checksum


        vsa.space_id = #{spaceId}


        AND vsa.type = #{type}


AND vsa.cite = 0
AND vsa.created_at >= #{startAt}
AND vsa.created_at < #{endAt}
AND vsa.is_deleted = 0
AND va.is_deleted = 0
AND vsa.created_at = vsa.updated_at

mapper.update:
UPDATE vika_space_asset SET is_deleted = 1 WHERE id IN
#{item}
AND is_deleted = 0
