mapper.select:
SELECT dst_id, meta_data FROM vika_datasheet_meta WHERE dst_id IN
#{item}
AND is_deleted = 0

SELECT MIN(id)
FROM vika_datasheet_changeset
WHERE created_at >= #{createdAt}

SELECT vdm.id, vdm.dst_id, JSON_EXTRACT(vdm.meta_data -> '$.fieldMap.*','$[*]') AS fieldMap FROM vika_datasheet_meta vdm
, vika_node vn
vdm.dst_id = vn.node_id
AND JSON_CONTAINS_PATH(vdm.meta_data -> '$.fieldMap.*', 'all','$[*].property.brotherFieldId') = 1 AND JSON_CONTAINS(vdm.meta_data -> '$.fieldMap.*.type', '7', '$') = 1 AND vdm.is_deleted = 0
AND vn.type = 2 AND vn.is_deleted = 0 AND vn.space_id = #{spaceId}
AND vdm.id > #{nextId}
