mapper.select:
SELECT node_id
FROM vika_node
WHERE is_rubbish = 0 AND space_id IN

    #{item}


    AND `type` = #{type}

SELECT vn.space_id, vn.node_id, vn.updated_at
FROM vika_node vn
JOIN vika_space_asset vsa ON vn.node_id = vsa.node_id AND vsa.is_deleted = 0

    AND vsa.space_id = #{spaceId}

WHERE vn.is_rubbish = 1 AND vn.is_deleted = 0 AND vn.updated_at < #{deadline}
GROUP BY vn.node_id

SELECT space_id
FROM vika_node
WHERE node_id = #{nodeId}
