#{item}

mapper.select:
SELECT vnp.id
FROM vika_node_permission vnp
WHERE vnp.id > #{minId}
  AND (node_id, unit_id) NOT IN
      (SELECT control_id, unit_id FROM vika_control_role);

mapper.update:
UPDATE vika_control_role
SET role_code  = #{roleCode},
    updated_at = #{updatedAt}
WHERE id = #{id}

