mapper.select:
SELECT * FROM vika_billing_subscription WHERE subscription_id IN
#{item}
AND is_deleted = 0

SELECT * FROM vika_billing_subscription WHERE bundle_id IN
#{item}
AND is_deleted = 0

SELECT *
FROM vika_billing_subscription
WHERE bundle_id = #{bundleId}
  AND state = #{state.name}
  AND is_deleted = 0

mapper.update:
UPDATE vika_billing_subscription
SET is_deleted = #{isDeleted}
WHERE subscription_id IN

    #{item}

mapper.select:
SELECT product_category, plan_id, metadata, expire_date
FROM vika_billing_subscription
WHERE space_id = #{spaceId}
  AND state = #{state.name}
  AND TIMESTAMPDIFF(SECOND,NOW(), expire_date) >= 0
  AND is_deleted = 0

SELECT COUNT(*)
FROM vika_billing_subscription
WHERE space_id = #{spaceId}
  AND plan_id = #{planId}
  AND state = #{state.name}
  AND TIMESTAMPDIFF(SECOND,NOW(), expire_date) >= 0
  AND is_deleted = 0
  AND meatdata IS NOT NULL

SELECT COUNT(*)
FROM vika_billing_subscription
WHERE space_id = #{spaceId}
  AND product_category = #{category.name}
  AND state = #{state.name}
  AND TIMESTAMPDIFF(SECOND,NOW(), expire_date) >= 0
  AND is_deleted = 0
