mapper.select:
SELECT COUNT(*)
FROM vika_social_tenant_bind
WHERE space_id = #{spaceId}

SELECT COUNT(*)
FROM vika_social_tenant_bind
WHERE tenant_id = #{tenantId}

SELECT tenant_id
FROM vika_social_tenant_bind
WHERE space_id = #{spaceId}

SELECT space_id
FROM vika_social_tenant_bind
WHERE tenant_id = #{tenantId}

mapper.delete:
DELETE
FROM vika_social_tenant_bind
WHERE space_id = #{spaceId} AND tenant_id = #{tenantId}

mapper.select:
SELECT COUNT(*)
FROM vika_social_tenant_bind
WHERE tenant_id = #{tenantId}
AND app_id = #{appId}

SELECT space_id
FROM vika_social_tenant_bind
WHERE tenant_id = #{tenantId}
AND app_id = #{appId}
LIMIT 0,1

SELECT *
FROM vika_social_tenant_bind
WHERE tenant_id = #{tenantId}
AND app_id = #{appId}
LIMIT 0,1

SELECT COUNT(*)
FROM vika_social_tenant_bind vstb
LEFT JOIN vika_social_tenant vst on vstb.app_id = vst.app_id
WHERE vstb.space_id = #{spaceId}
AND vst.platform = #{platform}

SELECT space_id
FROM vika_social_tenant_bind
WHERE tenant_id = #{tenantId}
AND app_id = #{appId}

SELECT space_id
FROM vika_social_tenant_bind vstb
JOIN vika_social_tenant vst ON vst.tenant_id = vstb.tenant_id
WHERE vst.platform = #{platformType.value}
AND vst.app_type = #{appType.type}
AND vst.status = 1

SELECT space_id FROM vika_social_tenant_bind
WHERE tenant_id IN

    #{tenantId}

AND app_id IN

    #{appId}
