mapper.select:
SELECT union_id
FROM vika_third_party_member
WHERE app_id = #{appId} AND open_id = #{openId} AND type = #{type}

SELECT nick_name
FROM vika_third_party_member
WHERE app_id = #{appId} AND union_id = #{unionId} AND type = #{type}

SELECT extra
FROM vika_third_party_member
WHERE id = #{id}

SELECT session_key
FROM vika_third_party_member
WHERE id = #{id}

SELECT id, nick_name, avatar
FROM vika_third_party_member
WHERE app_id = #{appId} AND union_id = #{unionId} AND type = #{type}

SELECT vu.id
FROM vika_third_party_member vtpm
JOIN vika_user_link vul ON vtpm.union_id = vul.union_id AND vul.type = #{linkType}
JOIN vika_user vu ON vu.id = vul.user_id AND vu.is_deleted = 0
WHERE vtpm.id = #{id}
